import { Component } from '@angular/core';
import { Customer } from 'src/app/models/customer';
import { CustomerService } from 'src/app/service/customer.service';
import { ToastrService } from 'ngx-toastr';
import { Router } from '@angular/router';

@Component({
  selector: 'app-new-customer',
  templateUrl: './new-customer.component.html',
  styleUrls: ['./new-customer.component.css']
})
export class NewCustomerComponent {

  companyName = '';
  contactName = '';
  contactTitle = '';
  address = '';
  city = '';
  region = '';
  postalCode = '';
  country = '';
  phone = '';
  fax = '';


  constructor(
    private customerService: CustomerService,
    private toastr: ToastrService,
    private router: Router
    ){ }

  ngOnInit(){

  }

  onCreate(): void{
    const customer = new Customer(this.companyName, this.contactName, this.contactTitle, this.address, this.city, this.region, this.postalCode, this.country, this.phone, this.fax);
    this.customerService.save(customer).subscribe(
      data => {
        this.toastr.success('Cliente Creado con éxito', 'OK', {
          timeOut: 3000, positionClass: 'toast-top-center'
        });
        this.router.navigate(['/']);
      },
      err => {
        this.toastr.error(err.error.meensaje , 'Fail', {
          timeOut: 3000, positionClass: 'toast-top-center'
        });
        this.router.navigate(['/']);
      }
    )
  }
}

<div class="container">
    <div class="row mt-5">
        <div class="col-md-4 mx-auto">
            <form (ngSubmit)="onCreate()" novalidate #f="ngForm">
                <div class="form-group">
                    <label for="companyName">Nombre de la Empresa</label>
                    <input type="text" class="form-control" id="companyName" name="companyName"
                    [(ngModel)]="companyName" required>
                </div>
                <div class="form-group">
                    <label for="contactName">Nombre de Contacto</label>
                    <input type="text" class="form-control" id="contactName" name="contactName"
                    [(ngModel)]="contactName" required>
                </div>
                <div class="form-group">
                    <label for="contactTitle">Título del Contacto</label>
                    <input type="text" class="form-control" id="contactTitle" name="contactTitle"
                    [(ngModel)]="contactTitle" required>
                </div>
                <div class="form-group">
                    <label for="address">Dirección</label>
                    <input type="text" class="form-control" id="address" name="address"
                    [(ngModel)]="address" required>
                </div>
                <div class="form-group">
                    <label for="city">Ciudad</label>
                    <input type="text" class="form-control" id="city" name="city"
                    [(ngModel)]="city" required>
                </div>
                <div class="form-group">
                    <label for="region">Region</label>
                    <input type="text" class="form-control" id="region" name="region"
                    [(ngModel)]="region" required>
                </div>
                <div class="form-group">
                    <label for="postalCode">Código Postal</label>
                    <input type="text" class="form-control" id="postalCode" name="postalCode"
                    [(ngModel)]="postalCode" required>
                </div>
                <div class="form-group">
                    <label for="country">País</label>
                    <input type="text" class="form-control" id="country" name="country"
                    [(ngModel)]="country" required>
                </div>
                <div class="form-group">
                    <label for="phone">Teléfono</label>
                    <input type="text" class="form-control" id="phone" name="phone"
                    [(ngModel)]="phone" required>
                </div>
                <div class="form-group">
                    <label for="fax">Fax</label>
                    <input type="text" class="form-control" id="fax" name="fax"
                    [(ngModel)]="fax" required>
                </div>
                <button class="btn btn-outline-success btn-block" [disabled]="!f.valid">Crear Cliente</button>
            </form>
        </div>
    </div>
</div>
